package Strings;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class AllAnagramInString {


    public static void main(String[] args) {
        
        String s="\"";
        String p="a";
        List<Integer> ans= findAnagrams1(s,p);
        System.out.println(ans);
    }  
    public  static List<Integer> findAnagrams(String s, String p) {
        List<Integer> result=new ArrayList<>();String sub; char[] p1=p.toCharArray();Arrays.sort(p1);
         for(int i=0;i<=s.length();i++){
          for (int j = i+1; j < s.length()+1; j++) {
          sub=s.substring(i, j);
          char[] sub1=sub.toCharArray();Arrays.sort(sub1);
          if(Arrays.equals(sub1,p1)){
            result.add(i);
          }
          }
         }
        return result;
    }
    
    public  static List<Integer> findAnagrams1(String s, String p){
        List<Integer> result = new ArrayList<>();
        int pLen = p.length();
        char[] p1 = p.toCharArray();
        Arrays.sort(p1);

        for (int i = 0; i <= s.length() - pLen; i++) {  // only go up to s.length() - p.length()
            String sub = s.substring(i, i + pLen);        // substring of exact length
            char[] sub1 = sub.toCharArray();
            Arrays.sort(sub1);
            if (Arrays.equals(sub1, p1)) {
                result.add(i);
            }
        }

        return result;
    }


}
